/* eslint-env mocha, browser */
import {strictEqual} from 'assert';
import CoveredClass from '.';

describe('CoveredClass', () => {
	it('Basic Tests', () => {
		const myobj = new CoveredClass();

		strictEqual(typeof myobj.instancePropertyBefore, 'string', 'instancePropertyBefore is a String');
		strictEqual(typeof myobj.instancePropertyAfter, 'string', 'instancePropertyAfter is a String');

		console.log('[boundArrow]', myobj.boundArrow('AAAAA '));
		console.log('[boundFunction]', myobj.boundFunction('BBBBB '));

		console.log('[staticProperty]', CoveredClass.staticProperty);
		console.log('[staticArrow]', CoveredClass.staticArrow('CCCCC '));
		console.log('[staticFunction]', CoveredClass.staticFunction('DDDDD '));
	});
});
